import React, { useState } from "react";
import { StyleSheet, Text, View } from "react-native";

import { AdicionarTarefa } from "../../componentes/AdicionarTarefa";
import { ListaTarefas } from "../../componentes/ListaTarefas";

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#4F4F4F",
    paddingTop: 40,

    marginTop:5,
  },
  titulo: {
    fontSize: 18,
    fontWeight: "bold",
    marginTop: 40,
    marginBottom: 8,
    marginHorizontal: 16,
   textAlign:"center"

  }, titulo2: {
    fontSize: 18,
    fontWeight: "bold",
    marginTop: 40,
    marginBottom: 8,
    marginHorizontal: 16,


  },
  tarefasConcluidas: {
    fontSize: 16,
    marginHorizontal: 16,
    marginBottom: 16,
  },
  adicionarTarefaContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginHorizontal: 16,
    marginBottom: 16,
  },
  adicionarTarefaInput: {
    flex: 1,
    borderWidth: 1,
    borderColor: "lightgray",
    borderRadius: 4,
    paddingHorizontal: 8,
    marginRight: 8,
  },
});

type TarefaState = {
  id: number;
  titulo: string;
  concluida: boolean;
};

export const Principal = () => {
  const [tarefas, setTarefas] = useState<TarefaState[]>([]);

  const adicionarTarefa = (titulo: string) => {
    const novaTarefa: TarefaState = {
      id: Date.now(),
      titulo,
      concluida: false,
    };
    setTarefas([...tarefas, novaTarefa]);
  };

  const concluirTarefa = (id: number) => {
    setTarefas((prevTarefas) =>
      prevTarefas.map((tarefa) => {
        if (tarefa.id === id) {
          return { ...tarefa, concluida: !tarefa.concluida };
        } else {
          return tarefa;
        }
      })
    );
  };

  const removerTarefa = (id: number) => {
    setTarefas((prevTarefas) =>
      prevTarefas.filter((tarefa) => tarefa.id !== id)
    );
  };

  const tarefasConcluidas = tarefas.filter((tarefa) => tarefa.concluida).length;

  return (
    <View style={styles.container}>
     <Text style={styles.titulo}>Lista de Tarefas</Text>
      <AdicionarTarefa
        tarefas={tarefas.map((tarefa) => tarefa.titulo)}
        onAdicionarTarefa={adicionarTarefa}
      />
       <Text style={styles.tarefasConcluidas}>
        {tarefasConcluidas} Numero de Tarefas Prontas
      </Text>

      <Text style={styles.titulo2}>Tarefas:</Text>
      <ListaTarefas
        tarefas={tarefas.filter((tarefa) => !tarefa.concluida)}
        onConcluirTarefa={concluirTarefa}
        onRemoverTarefa={removerTarefa}
      />

      <Text style={styles.titulo2}>Tarefas Prontas:</Text>
      <ListaTarefas
        tarefas={tarefas.filter((tarefa) => tarefa.concluida)}
        onConcluirTarefa={concluirTarefa}
        onRemoverTarefa={removerTarefa}
      />
    </View>
  );
};
